import React from "react";
class Greeting {
  render() {
    const date = new Date();
    const hour = date.getHours();
    let timeOfDay;
    hour % 12 < 12
      ? (timeOfDay = "Morning")
      : hour % 12 > 17
      ? (timeOfDay = "Night")
      : (timeOfDay = "Afternoon");

    return (
      <div>
        <p>Good {timeOfDay}</p>
      </div>
    );
  }
}
export default Greeting;
