import { React, Component } from "react";

class Greeting extends React.Component {
  render() {
    const date = new Date();
    const hour = date.getHours();
    let timeOfDay;
    hour % 12 < 12
      ? (timeOfDay = "Morning")
      : hour % 12 > 17
      ? (timeOfDay = "Night")
      : (timeOfDay = "Afternoon");

    return <p>Good {timeOfDay}!</p>;
  }
}
export default Greeting;
